// page에 들어갈 컴포넌트

import React, { useState } from "react";
import Link from "next/link";
// import Image from "next/image";
import { Menu, Input, Button, Carousel, Row, Col } from "antd";
import PropTypes from "prop-types";

const AppLayout = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item key="home">
          <Link href="/">
            <a>랜딩페이지</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="signup">
          <Link href="/signup">
            <a>회원가입</a>
          </Link>
        </Menu.Item>
      </Menu>
      {/* <Carousel>
        <div style={{ width: "100%" }}>
          <Image src="../main_web.png" />
        </div>
        <div>
          <Image src="../main_web.png" />
        </div>
      </Carousel> */}
      <Row>{isLoggedIn ? <로그인한상태에서 보여줄 화면 /> : <LoginForm />}</Row>
      <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
        <Col xs={24} md={8}>
          <a href="/" target="_self">
            우리의 여행
          </a>
        </Col>
        <Col xs={24} md={8}>
          우리의 순간
        </Col>
        <Col xs={24} md={8}>
          심플 슬라이드
        </Col>
      </Row>

      {children}
    </div>
  );
};
AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
  //   리액트에서 return () 안에 들어가는, 즉 화면에 표시되는 것, 을 node 라고 한다
};

export default AppLayout;
