// page에 들어갈 컴포넌트

import React from "react";
import Link from "next/link";
import { Menu, Input, Button, Carousel } from "antd";
import PropTypes from "prop-types";

const contentStyle = {
  height: "160px",
  color: "#fff",
  lineHeight: "160px",
  textAlign: "center",
  background: "#364d79",
};

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item key="home">
          <Link href="/">
            <a>랜딩페이지</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="signup">
          <Link href="/signup">
            <a>회원가입</a>
          </Link>
        </Menu.Item>
      </Menu>
      <Carousel>
        <div>
          <img src="../main_web.png"></img>
        </div>
        <div>
          <img src="../main_web.png"></img>
        </div>
      </Carousel>
      {children}
    </div>
  );
};
AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
  //   리액트에서 return () 안에 들어가는, 즉 화면에 표시되는 것, 을 node 라고 한다
};

export default AppLayout;
